import React,{useState,useEffect} from 'react';
import Axios from 'axios';
import './App.css';

function App() {
  const [FirstName, setFirstName]=useState("");
  const [Email,setEmail]=useState("");
   const [userInsertlist,setUserList]=useState([]);
  useEffect(() =>{
    Axios.get("http://localhost:3009/api/get").then((response)=>{
      setUserList(response.data)
    });
  },[]);


  const submitUser=()=>{
  Axios.post("http://localhost:3009/api/insert", {
    FirstName:FirstName,
    Email:Email,

  }).then(()=>{
    alert("Successful insert");
  });
  };

  return (
    <div className="App">
      <h1> Crud Application</h1>
      <div className="form">
        <label>First name</label>
        <input type ="text" name="FirstName"
        onChange={(e)=>{
          setFirstName(e.target.value);
        }}
        />
        <label>Email</label>
        <input type="email" name="Email"
        onChange={(e)=>{
          setEmail(e.target.value);
        }}

        />
      <button onClick={submitUser}>Submit </button>
      {userInsertlist.map((val)=>{
        return <h1> FirstName:{val.Firstname} | Email:{val.Email}
        </h1>
      })}      
      </div>


      </div>
  );
}

export default App;
