import React, { useCallback, useDebugValue } from 'react';
import ReactDOM from 'react-dom';
import {useState,useEffect} from 'react';
import {Link, Route, BrowserRouter as Router, Redirect,Switch } from 'react-router-dom';
import Axios from 'axios';
import Signup from './signup';

//import done from './done';
import {useHistory} from 'react-router-dom';
import{useParams}from 'react-router-dom';
function Signin(){
  const [userInsertlist,setUserList]=useState([]);
 const [newFirstName,setnewFirstName]=useState("");
 const [newLastName,setnewLastName]=useState("");
 const [newPassword,setnewPassword]=useState("");
 const [newEmail,setnewEmail]=useState("");
 let history=useHistory();
 let {id} =useParams();
   const [FirstName, setFirstName]=useState('');
   //const{history}=this.props;
    const [Email,setEmail]=useState('');
    const login=()=>{
        Axios.post("http://localhost:3019/api/login", {
          FirstName:FirstName,
          Email:Email
        
        }).then((response) => {
          if (!response.data.error) {
              console.log('successful signup')
              //history.push('/home');

             
              setUserList(response.data)
            Number1();
               alert("Successfully signed in")
              //history.push("/done");
          } else {
            alert("Wrong email address or First name, Please try again!")
           }

          // Redirect
         // this.props.history.replace('/signup');
      })
  /*.catch(error => {
          console.log('signup error: ')
          console.log(error)
      })*/

        
        Array.from(document.querySelectorAll("input")).forEach(
          input => (input.value = "")
        )
        
    }
    function Number1(){
      console.log("working")
      
      //setUserList(response)
      //console.log(JSON.stringify(response));
      {userInsertlist.map((val)=>{
        return (
        <div className="card">
         
         <p> First Name:{val.FirstName}<br/>
             Last name:{val.LastName}<br/>
             Email address:{val.Email}<br/>
            </p>
        <button onClick={()=>{deleteUser(val.Email)}}> Delete user</button><br/>
        <h2> fill in the following form to update your entries!</h2>
        <label>First name: </label><input type="text" name="newFirstName" onChange={(e)=>{
          setnewFirstName(e.target.value)
        }}/>
        <label>Last name</label>
         <input type="text" name="newLastName" onChange={(e)=>{
          setnewLastName(e.target.value)
        }}/>
        <label>Email: </label>
         <input type="email" name="nEmail" onChange={(e)=>{
          setnewEmail(e.target.value)
        }}/>
        
        <label>Password: </label>
         <input type="password" name="newPassword" onChange={(e)=>{
          setnewPassword(e.target.value)
        }}/>
        <button onClick={()=>{updateUser(val.Email)}}> Update</button>
        </div>
        
        
        )})}
      /*{userInsertlist.map((val)=>{
       
          FirstName:{val.FirstName} 
          Email:{val.Email}
         console.log(FirstName )
      })}*/


    }
    const deleteUser=(Email)=>{
      Axios.delete(`http://localhost:3019/api/delete/${Email}`);
      alert("Successfully deleted your account!")
      
    };
    const updateUser=(Email)=>{
      Axios.put("http://localhost:3019/api/update",{
        oEmail:Email,
        nEmail:newEmail,
        FirstName:newFirstName,
        LastName:newLastName,
        Password:newPassword
      });
      Array.from(document.querySelectorAll("input")).forEach(
        input => (input.value = "")
      )
    alert("Succesfully changed")
    }
    
    return(
      
        <div className="App">
    
        <h1> fill in those forms </h1>
     <div className="form">
         <label>First Name: </label>
         <input type="text" name="FirstName"
         onChange={(e)=>{
            setFirstName(e.target.value);
          }}/>
         <label> Email: </label>
         <input type="email" name="Email"
         onChange={(e)=>{
            setEmail(e.target.value);
          }}/>
         <button onClick={login}>Login</button><br/>
         



     </div>
     </div>
    );
};


export default Signin;

