import React from 'react';
import ReactDOM from 'react-dom';
import {useState,useEffect} from 'react';
import Axios from 'axios';

function Signup(){
    
var [FirstName, setFirstName]=useState("");
    const [LastName, setLastName]=useState("");
    const [Password, setPassword]=useState("");
    const [Email,setEmail]=useState("");
     const [userInsertlist,setUserList]=useState([]);
    const [newFirstName,setnewFirstName]=useState("");
   

    const submitUser=()=>{
        Axios.post("http://localhost:3019/api/insert", {
          
          FirstName:FirstName,
          Email:Email,
          LastName:LastName,
          Password:Password,
        
        }).then((response) => {
          if (!response.data.error) {
              console.log('successful signup')
              setUserList(response.data)
            //Number1(response.data);
               alert("Successfully created account")
               {userInsertlist.map((val)=>{
                return (
                <div className="card">
                 
                 <p> First Name:{val.FirstName}<br/>
                     Last name:{val.LastName}<br/>
                     Email address:{val.Email}<br/>
                     Password:{val.Password}
                    </p></div>)})}
          } else {
            alert(response)
                 alert("Please fill in all the forms")
            //salert("Wrong email address or First name, Please try again!")
           }

         
      }) 
      
        Array.from(document.querySelectorAll("input")).forEach(
            input => (input.value = "")
          );
       
    };
    return(
        <div className="App">
            <h1> Please fill in those forms to create account</h1>
            <div className="form">
                <label>First Name </label>
                <input type="text" name="FirstName" required="required"
                onChange={(e)=>{
                    setFirstName(e.target.value);
                  }}/>
                <label>Last Name</label>
                <input type="text" name="LastName" required
                onChange={(e)=>{
                    setLastName(e.target.value);
                    
                  }}/>
                <label >Email</label>
               <input type="email" name="Email" required
               onChange={(e)=>{
                setEmail(e.target.value);
              }}/>
               <label> Password</label> 
               <input type="password" name="Password" required
               onChange={(e)=>{
                setPassword(e.target.value);
              }}/>
               <button onClick={submitUser}> Create</button>
          
            </div>
        </div>
    )
}






  export default Signup;
