import {useState,useEffect} from 'react';
import Axios from 'axios';
import Signin from './signin';
import Home from './home';
import './App.css';
import {Link, Switch} from 'react-router-dom';
import { push } from 'react-router-redux';
import {useHistory} from 'react-router-dom';
import React,{ Fragment } from "react";
import { Route, BrowserRouter as Router } from "react-router-dom";

function App() {
  const [state,setState]=useState(true)
  const [FirstName, setFirstName]=useState("");
  const [Email,setEmail]=useState("");
   const [userInsertlist,setUserList]=useState([]);
  const [newFirstName,setnewFirstName]=useState("");
  const history=useHistory();
   useEffect(() =>{
    Axios.get("http://localhost:3009/api/get").then((response)=>{
      setUserList(response.data)
    });
  },[]);


  const submitUser=()=>{
  Axios.post("http://localhost:3009/api/insert", {
    FirstName:FirstName,
    Email:Email,

  });

   setUserList([...userInsertlist,{FirstName: FirstName, Email:Email},]);

  };
  const deleteUser=(Email)=>{
    Axios.delete('http://localhost:3009/api/delete/${Email}');
  };
  function signup(){
    alert("To signup");
  }
  function signin(){

    alert('hello')
    };
  const updateUser=(Email)=>{
    Axios.put("http://localhost:3009/api/update",{
      Email:Email,
      FirstName:newFirstName
    });
    setnewFirstName("")
  }
  return (
    <div className="App">
      <Router>
      <Switch>

      <Route path="/" exact component={Home}/>
      <Route path="/signin"  component={Signin}/>
      </Switch>
      </Router>
      <button >Signup</button>
      <div className="form">
        <label>First name</label>
        <input type ="text" name="FirstName"
        onChange={(e)=>{
          setFirstName(e.target.value);
        }}
        />
        <label>Email</label>
        <input type="email" name="Email"
        onChange={(e)=>{
          setEmail(e.target.value);
        }}

        />
      <button onClick={submitUser}>Submit </button>
      {userInsertlist.map((val)=>{
        return (
        <div className="card">
         <h1> FirstName:{val.FirstName} </h1>
         <p> Email:{val.Email}  </p>
       <button onClick={()=>{deleteUser(val.Email)}}> Delete </button>
       <input type="text" id="updateInput" onChange={(e)=>{
         setnewFirstName(e.target.value)
       }}/>
       <button onClick={()=>{updateUser(val.Email)}}> Update</button>

        </div>

        );  
    })}      
      </div>


      </div>
  );
}

export default App;
